<div class="progresally-event-objective-selection">
	<input type="checkbox" progressally-param="{{variable-name}}[]" {{checkbox-attr}} value="{{objective-id}}" id="progressally-{{type}}-objective-selection-{{event-id}}-{{page-id}}-{{objective-id}}" />
	<label class="progresally-event-objective-selection {{label-class}}" {{label-attr}} for="progressally-{{type}}-objective-selection-{{event-id}}-{{page-id}}-{{objective-id}}">{{description}}</label>
</div>